    convertDateTo24Hour: function(date){
        var elem = date.split(" ");
        var stSplit = elem[0].split(":");// alert(stSplit);
        var stHour = stSplit[0];
        var stMin = stSplit[1];
        var stAmPm = elem[1].toUpperCase();
        var newhr = 0;
        var ampm = '';
        var newtime = '';
      
        if (stAmPm=='PM')
        { 
            if (stHour!=12)
            {
                stHour=stHour*1+12;
            }
           
        }else if(stAmPm=='AM' && stHour=='12'){
           stHour = stHour -12;
        }else{
            stHour=stHour;
          }
        if(arguments.length==2){
           // return stHour+':'+stMin;
            return "Kl "+stHour;
        }else{
            return stHour+':'+stMin+":00";
        }
    },
